import React, { createContext, useContext, useEffect, useState } from "react";
import axios from "axios";

const StocksContext = createContext();

export const useStocks = () => {
    return useContext(StocksContext);
};

const StocksProvider = ({ children }) => {
    const [stocks, setStocks] = useState([]);

    const fetchStockPrice = async (symbol) => {
        try {
            const response = await axios.get(`http://localhost:5000/api/stock-price/${symbol}`);
            return response.data;
        } catch (error) {
            console.error(`Error fetching price for ${symbol}:`, error.message);
            return null;
        }
    };

    
    const updateStockPrices = async (updatedStocks) => {
        const updatedStockData = await Promise.all(
            updatedStocks.map(async (stock) => {
                const stockPriceData = await fetchStockPrice(stock.symbol);
                const currentPrice = stockPriceData ? stockPriceData.currentPrice : stock.currentPrice;
                const profitLoss = stockPriceData
                    ? ((currentPrice - stock.buyPrice) * stock.qty).toFixed(2)
                    : stock.profitLoss;
                return {
                    ...stock,
                    currentPrice,
                    profitLoss,
                };
            })
        );
        setStocks(updatedStockData);
    };

    useEffect(() => {
        updateStockPrices(stocks);
    }, []);
    

    const addStock = async (newStock) => {
        const updatedStocks = [...stocks, newStock];
        await updateStockPrices(updatedStocks);
    };

    const deleteStock = async (symbol) => {
        const updatedStocks = stocks.filter((stock) => stock.symbol !== symbol);
        await updateStockPrices(updatedStocks);
    };

    const editStock = async (updatedStock) => {
        const updatedStocks = stocks.map((stock) =>
            stock.symbol === updatedStock.symbol ? updatedStock : stock
        );
        await updateStockPrices(updatedStocks);
    };

    return (
        <StocksContext.Provider value={{stocks, addStock, deleteStock, editStock}}>
            {children}
        </StocksContext.Provider>

    );
}

export default StocksProvider;


