import React, { createContext, useContext, useEffect, useState } from "react";
import axios from "axios";

const StocksContext = createContext();

export const useStocks = () => {
    return useContext(StocksContext);
};

const StocksProvider = ({ children }) => {
    const [stocks, setStocks] = useState([
        {
            symbol: 'TSLA',
            name: 'Tesla Inc',
            qty: 10,
            profitLoss: 1000,
            currentPricePerStock: null,
        },
        {
            symbol: 'AAPL',
            name: 'Apple Inc',
            qty: 15,
            profitLoss: 1200,
            currentPricePerStock: null,
        },
    ]);

    const fetchStockPrice = async (symbol) => {
        try {
            const response = await axios.get(`http://localhost:5000/api/stock-price/${symbol}`);
            return response.data;
        } catch (error) {
            console.error(`Error fetching price for ${symbol}:`, error.message);
            return null;
        }
    };

    useEffect(() => {
        const updatedStockPrices = async () => {
            const updatedStocks = await Promise.all(
                stocks.map(async (stock) => {
                    const stockPriceData = await fetchStockPrice(stock.symbol);
                    return {
                        ...stock,
                        currentPrice: stockPriceData ? stockPriceData.currentPrice : stock.currentPrice,
                    };
                })
            );
            setStocks(updatedStocks);
        };

        updatedStockPrices();
    },[]);

    const addStock = (newStock) => {
        setStocks((prevStocks) => [...prevStocks, newStock]);
    };

    const deleteStock = (symbol) => {
        setStocks((prevStocks) => prevStocks.filter((stock) => stock.symbol !== symbol));
    };

    const editStock = (updatedStock) => {
        setStocks((prevStocks) =>
            prevStocks.map((stock) =>
                stock.symbol === updatedStock.symbol ? updatedStock : stock
            )
        );
    };

    return (
        <StocksContext.Provider value={{stocks, addStock, deleteStock, editStock}}>
            {children}
        </StocksContext.Provider>

    );
}

export default StocksProvider;
