import React from "react";

const PortFolioSummary = ({ stocks }) => {
    if (!stocks || stocks.length === 0) {
        return <div>No stocks available</div>; // Handle empty or undefined stocks
    }

    const totalPortfolioValue = stocks.reduce((sum, stock) => 
        stock.currentPrice ? sum + (stock.qty * stock.currentPrice) : sum, 0
    );

    const bestPerformer = stocks.reduce((best, stock) => 
        (stock.profitLoss > best.profitLoss) ? stock : best, stocks[0]
    );

    return (
        <div className="portfolio_summary">
            <h3>Total Portfolio Value: ${totalPortfolioValue}</h3>
            <h3>Best Performer: {bestPerformer?.name || "N/A"} (${bestPerformer?.profitLoss || 0} profit)</h3> 
        </div>
    );
};

export default PortFolioSummary;
