import React from "react";

const PortFolioSummary = ({ stocks }) => {
    // Handle empty stocks array and missing currentPrice
    const totalPortfolioValue = stocks.reduce((sum, stock) => {
        const currentPrice = stock.currentPrice || 0; // Default to 0 if currentPrice is missing
        return sum + (stock.qty * currentPrice);
    }, 0);

    const bestPerformer = stocks.length > 0 
        ? stocks.reduce((best, stock) => (stock.profitLoss > best.profitLoss ? stock : best), stocks[0])
        : null; // Handle empty array

    return (
        <div className="portfolio_summary">
            <h3>Total Portfolio Value: ${totalPortfolioValue.toFixed(2)}</h3>
            {bestPerformer ? (
                <h3>Best Performer: {bestPerformer.name} (${bestPerformer.profitLoss.toFixed(2)} profit)</h3>
            ) : (
                <h3>No stocks available</h3>
            )}
        </div>
    );
};

export default PortFolioSummary;
